/* ===== 标题颜色===== */
/* 1. 编辑/预览模式标题颜色 */
.protyle-wysiwyg .h1 {
color: #eb8383 !important; /* 一级标题：#eb8383 */
}
.protyle-wysiwyg .h2 {
color: #8a7da0 !important; /* 二级标题：#8a7da0 */
}
.protyle-wysiwyg .h3 {
color: #7db5cd !important; /* 三级标题：#7db5cd */
}
.protyle-wysiwyg .h4 {
color: #71a796 !important; /* 四级标题：#71a796 */
}
.protyle-wysiwyg .h5 {
color: #b6a277 !important; /* 五级标题：#b6a277 */
}
.protyle-wysiwyg .h6 {
color: #dda36a !important; /* 六级标题：#dda36a */
}

/* 2. PDF导出/打印模式（强制保留颜色） */
@media print {
.protyle-wysiwyg .h1, .protyle-print .h1 {
color: #eb8383 !important;
-webkit-print-color-adjust: exact !important;
print-color-adjust: exact !important;
}
.protyle-wysiwyg .h2, .protyle-print .h2 {
color: #8a7da0 !important;
-webkit-print-color-adjust: exact !important;
print-color-adjust: exact !important;
}
.protyle-wysiwyg .h3, .protyle-print .h3 {
color: #7db5cd !important;
-webkit-print-color-adjust: exact !important;
print-color-adjust: exact !important;
}
.protyle-wysiwyg .h4, .protyle-print .h4 {
color: #71a796 !important;
-webkit-print-color-adjust: exact !important;
print-color-adjust: exact !important;
}
.protyle-wysiwyg .h5, .protyle-print .h5 {
color: #b6a277 !important;
-webkit-print-color-adjust: exact !important;
print-color-adjust: exact !important;
}
.protyle-wysiwyg .h6, .protyle-print .h6 {
color: #dda36a !important;
-webkit-print-color-adjust: exact !important;
print-color-adjust: exact !important;
}
}
